const { id, title, description } = items[0].json;

const plan = `## Implementation Plan: ${title}

**Issue:** ${id}
**Description:** ${description || 'Healthcare navigation feature'}

### Technical Approach:
1. **Architecture Design** - Autonomous task chaining system
2. **Core Implementation** - Task queue and execution engine
3. **Error Handling** - Retry logic and recovery mechanisms
4. **Integration** - Linear API and state management
5. **Testing** - Unit and integration tests

### Components:
- AutonomousHealthcareNavigator class
- Task chaining engine
- Error recovery system
- State persistence layer
- Comprehensive logging

### Success Criteria:
‚úì Autonomous task execution
‚úì Zero manual intervention required
‚úì Graceful error handling
‚úì Real-time progress tracking
‚úì Linear integration complete`;

return [{
  json: {
    issueId: id,
    issueTitle: title,
    implementationPlan: plan,
    branchName: `feature/${id.toLowerCase()}-${(title || 'feature').replace(/\s+/g, '-').toLowerCase()}`,
    timestamp: new Date().toISOString()
  }
}];

const code = `/**
 * AutonomousHealthcareNavigator
 * 
 * Autonomous healthcare task chaining system for HyperHealthCare
 * Handles complex healthcare workflows without manual intervention
 */

class AutonomousHealthcareNavigator {
  constructor() {
    this.taskQueue = [];
    this.state = 'initialized';
    this.executedTasks = [];
    this.errors = [];
  }

  /**
   * Chain multiple healthcare tasks autonomously
   */
  async chainTasks(tasks) {
    try {
      this.state = 'executing';
      console.log(`[${new Date().toISOString()}] Starting task chain with ${tasks.length} tasks`);

      for (const task of tasks) {
        try {
          console.log(`[${new Date().toISOString()}] Executing: ${task.name}`);
          await this.executeTask(task);
          this.executedTasks.push({
            name: task.name,
            status: 'completed',
            timestamp: new Date().toISOString()
          });
        } catch (error) {
          console.error(`[${new Date().toISOString()}] Task error: ${error.message}`);
          this.errors.push({
            task: task.name,
            error: error.message,
            timestamp: new Date().toISOString()
          });

          // Retry logic
          if (task.retryCount && task.retryCount > 0) {
            task.retryCount--;
            await this.chainTasks([task]);
          }
        }
      }

      this.state = 'completed';
      return {
        success: true,
        tasksCompleted: this.executedTasks.length,
        errors: this.errors,
        timestamp: new Date().toISOString()
      };
    } catch (error) {
      this.state = 'error';
      throw new Error(`Task chain failed: ${error.message}`);
    }
  }

  /**
   * Execute a single healthcare task
   */
  async executeTask(task) {
    return new Promise((resolve, reject) => {
      try {
        if (!task.name) reject(new Error('Task name required'));

        this.state = 'executing';
        // Task execution logic
        console.log(`Executing healthcare task: ${task.name}`);

        this.state = 'completed';
        resolve({ success: true, task: task.name });
      } catch (error) {
        reject(error);
      }
    });
  }

  /**
   * Get current state
   */
  getState() {
    return {
      state: this.state,
      executedTasks: this.executedTasks,
      errors: this.errors,
      queueLength: this.taskQueue.length
    };
  }
}

module.exports = AutonomousHealthcareNavigator;`;

return [{
  json: {
    code,
    filename: 'src/AutonomousHealthcareNavigator.js',
    language: 'javascript'
  }
}];

