const action = $parameter["action"];
const payload = $parameter["payload"] || {};

if (!/^[a-z_]+$/.test(action)) {
  throw new Error(`Invalid action: ${action}`);
}

const { exec } = require('child_process');
const util = require('util');
const execPromise = util.promisify(exec);

// NOTE: payload is single-quoted; escape any single quotes inside JSON
const payloadStr = JSON.stringify(payload).replace(/'/g, "\\'");

const cmd = `python3 '/mnt/n8n-nas/ClaudiaCodeDev/claudia_tools.py' ops ${action} --payload '${payloadStr}'`;

try {
  const { stdout } = await execPromise(cmd, { timeout: 300000 });
  const out = (stdout || "").trim();
  try {
    return JSON.parse(out);
  } catch (e) {
    // If tool returns non-JSON, pass through
    return { status: "ok", raw: out };
  }
} catch (error) {
  return { status: 'error', message: error.message };
}

