const config = {
  mode: 'latest',  // Options: 'latest', 'specific', 'active'
  specificPlanRunID: null,  // Set this if mode='specific'

  // HyperHealth project filter for testing
  projectID: '35708362-f541-46ae-b3b9-031e9beaf597',  // Set to null to process all projects
};
