// Filter and sort plan runs
let availablePlans = planRuns
  .map(item => item.json)
  .filter(plan => {
    // Exclude stopped plans
    if (plan.planStatus === 'stopped') return false;

    // Filter by projectID if configured
    if (config.projectID && plan.projectID !== config.projectID) return false;

    return true;
  });

// Sort by generatedAt (most recent first)
availablePlans.sort((a, b) => {
  const dateA = new Date(a.generatedAt || a.createdAt || 0);
  const dateB = new Date(b.generatedAt || b.createdAt || 0);
  return dateB - dateA;
});

if (availablePlans.length === 0) {
  throw new Error('No available plan runs found for the configured filters');
}

let selectedPlan;

switch (config.mode) {
  case 'latest':
    selectedPlan = availablePlans[0];
    break;

  case 'specific':
    if (!config.specificPlanRunID) {
      throw new Error('specificPlanRunID not set in config');
    }
    selectedPlan = availablePlans.find(p => p.planRunID === config.specificPlanRunID);
    if (!selectedPlan) {
      throw new Error(`Plan run not found: ${config.specificPlanRunID}`);
    }
    break;

  case 'active':
    // Look for plans marked as "active" or "approved"
    selectedPlan = availablePlans.find(p =>
      p.planStatus === 'approved' || p.planStatus === 'active'
    );
    if (!selectedPlan) {
      // Fallback to latest
      selectedPlan = availablePlans[0];
    }
    break;

  default:
    selectedPlan = availablePlans[0];
}

return [{
  json: {
    selectedPlanRunID: selectedPlan.planRunID,
    projectID: selectedPlan.projectID,
    planStatus: selectedPlan.planStatus,
    generatedAt: selectedPlan.generatedAt,
    totalPlansAvailable: availablePlans.length,
    selectionMode: config.mode,
    filterProjectID: config.projectID
  }
}];

