let complexityScore = 0;

// 1. Description Length (0-20 points)
const descLength = summary.length;
if (descLength < 100) complexityScore += 5;
else if (descLength < 300) complexityScore += 10;
else if (descLength < 600) complexityScore += 15;
else complexityScore += 20;

// 2. Keyword Analysis (0-40 points)
const complexKeywords = [
  'distributed', 'scalable', 'real-time', 'websocket', 'async', 'concurrent',
  'performance', 'optimization', 'refactor', 'architecture', 'microservice',
  'kubernetes', 'docker', 'ci/cd', 'pipeline', 'deployment', 'migration',
  'security', 'authentication', 'authorization', 'encryption', 'compliance',
  'algorithm', 'complex', 'advanced', 'sophisticated', 'enterprise'
];

const mediumKeywords = [
  'api', 'endpoint', 'database', 'query', 'model', 'validation', 'testing',
  'integration', 'component', 'service', 'handler', 'middleware', 'router',
  'controller', 'repository', 'orm', 'sql', 'nosql', 'redis', 'cache'
];

const text = (title + ' ' + summary).toLowerCase();
let complexCount = 0;
let mediumCount = 0;

complexKeywords.forEach(kw => {
  if (text.includes(kw)) complexCount++;
});

mediumKeywords.forEach(kw => {
  if (text.includes(kw)) mediumCount++;
});

complexityScore += Math.min(complexCount * 8, 30);
complexityScore += Math.min(mediumCount * 3, 10);

// 3. Label Analysis (0-20 points)
const epicLabels = ['epic', 'large', 'complex', 'critical', 'security'];
const mediumLabels = ['feature', 'enhancement', 'backend', 'frontend'];
const simpleLabels = ['bug', 'bugfix', 'hotfix', 'beginner', 'simple', 'documentation'];

labels.forEach(label => {
  const labelLower = label.toLowerCase();
  if (epicLabels.some(l => labelLower.includes(l))) complexityScore += 15;
  else if (mediumLabels.some(l => labelLower.includes(l))) complexityScore += 5;
  else if (simpleLabels.some(l => labelLower.includes(l))) complexityScore -= 10;
});

// 4. Multi-file indicator (0-20 points)
