// Filter for unprocessed or failed items
// Group by priority and complexity
const items = $input.all();

const workItems = items
  .filter(item => 
    item.json.status === 'pending' || 
    item.json.status === 'failed' ||
    (item.json.status === 'in_progress' && item.json.retry_count < 3)
  )
  .map(item => ({
    json: {
      issue_id: item.json.id,
      title: item.json.title,
      description: item.json.description || 'No description',
      labels: item.json.labels || [],
      priority: item.json.priority || 'medium',
      project_context: item.json.project_context || '',
      retry_count: item.json.retry_count || 0,
      previous_attempts: item.json.previous_attempts || [],
      phase: 'initial_implementation',
      packet_id: `PKT-${item.json.id}-${Date.now()}`
    }
  }));

return workItems;

// Build comprehensive prompt for initial implementation
const item = $input.first().json;

const systemPrompt = `You are an expert software engineer working on a healthcare automation platform.

Project Context:
${item.project_context}

Your task is to implement the following feature completely and correctly.

IMPORTANT REQUIREMENTS:
1. Write production-ready code with proper error handling
2. Include comprehensive type hints and documentation
3. Write pytest tests that actually validate correctness
4. Use modern Python best practices (3.11+)
5. Include edge case handling
6. No placeholder or TODO comments
7. All dependencies must be clearly specified
8. Code must be self-contained and runnable

DO NOT:
- Use deprecated APIs
- Make unverified claims about "production-ready" code
- Include logic errors or untested edge cases
- Use in-memory stores without documenting limitations
- Skip error handling

If you're unsure about an implementation detail, state your assumptions clearly.`;

const userPrompt = `# Issue: ${item.title}

## Description
${item.description}

## Requirements
- Provide complete file structure
- Include all necessary imports
- Write comprehensive tests
- Document assumptions and limitations
- Specify exact dependency versions

## Labels/Context
${item.labels.join(', ')}

Deliver:
1. Complete implementation with file structure
2. Test suite with coverage
3. README with setup instructions
4. requirements.txt or pyproject.toml
5. Metadata section with assumptions and next steps`;

return {
  json: {
    ...item,
    prompts: {
      system: systemPrompt,
      user: userPrompt
    },
    model_config: {
      model: 'gpt-oss-20b',
      temperature: 0.3,
      max_tokens: 8000
    }
  }
};

// Extract and format the implementation output
const item = $input.first();
const response = item.json.choices[0].message.content;
const metadata = $node["Build Initial Implementation Prompt"].json;

const timestamp = new Date().toISOString();
const outputFilename = `${metadata.packet_id}-initial-implementation.md`;

return {
  json: {
    ...metadata,
    implementation: {
      raw_output: response,
      timestamp: timestamp,
      filename: outputFilename,
      model: metadata.model_config.model,
      phase: 'validation'
    }
  }
};

// Build validation prompt for Claude Opus (better at code review)
const item = $input.first().json;
const implementation = item.implementation.raw_output;

const validationPrompt = `You are a senior software architect conducting a thorough code review.

Evaluate this implementation against these criteria:

## Code Quality Checklist
1. **Correctness**: Logic errors, off-by-one errors, incorrect algorithms
2. **Security**: Vulnerabilities, injection risks, insecure crypto
3. **Testing**: Test coverage, edge cases, assertion quality
4. **Dependencies**: Version specificity, deprecated APIs, compatibility
5. **Error Handling**: Missing try/catch, unhandled edge cases
6. **Documentation**: Accuracy of claims, missing assumptions
7. **Best Practices**: Code smells, anti-patterns, maintainability
8. **Completeness**: Missing files, incomplete implementations

## Implementation to Review

\`\`\`markdown
${implementation}
\`\`\`

## Required Output Format

Provide your evaluation as JSON:

\`\`\`json
{
  "overall_quality_score": <0-100>,
  "recommendation": "ACCEPT" | "ITERATE" | "REJECT",
  "critical_issues": [
    {"severity": "critical|major|minor", "category": "correctness|security|testing|etc", "description": "...", "location": "file:line or general"}
  ],
  "strengths": ["..."],
  "weaknesses": ["..."],
  "action_items": [
    {"priority": "high|medium|low", "task": "...", "rationale": "..."}
  ],
  "direction_assessment": "Does this align with project goals? Explain.",
  "suggested_next_steps": ["..."]
}
\`\`\`

Be thorough and honest. Production systems depend on this evaluation.`;

return {
  json: {
    ...item,
    validation_prompt: validationPrompt
  }
};

// Parse validation results
const item = $input.first();
const validationText = item.json.content[0].text;

// Extract JSON from markdown code block
const jsonMatch = validationText.match(/```json\n([\s\S]*?)\n```/);
let validation;

if (jsonMatch) {
  validation = JSON.parse(jsonMatch[1]);
} else {
  // Fallback: try to parse entire response
  try {
    validation = JSON.parse(validationText);
  } catch (e) {
    // If parsing fails, create error structure
    validation = {
      overall_quality_score: 0,
      recommendation: 'REJECT',
      critical_issues: [{
        severity: 'critical',
        category: 'validation_error',
        description: 'Failed to parse validation response',
        location: 'validation'
      }],
      error: e.message
    };
  }
}

const metadata = $node["Build Validation Prompt"].json;

return {
  json: {
    ...metadata,
    validation: validation,
    next_phase: validation.recommendation === 'ACCEPT' ? 'iteration' : 
                validation.recommendation === 'ITERATE' ? 'iteration' : 'correction'
  }
};

// ACCEPT PATH: Generate improvement and testing tasks
const item = $input.first().json;
const validation = item.validation;

const improvementTasks = [
  {
    type: 'comprehensive_testing',
    priority: 'high',
    description: 'Add integration tests and edge case coverage',
    prompt_context: `Original implementation accepted. Validation noted: ${validation.weaknesses.join('; ')}. Focus on: ${validation.action_items.filter(a => a.priority === 'high').map(a => a.task).join('; ')}`
  },
  {
    type: 'documentation',
    priority: 'medium',
    description: 'Complete API documentation and usage examples',
    prompt_context: 'Add comprehensive docstrings, README examples, and architecture docs'
  },
  {
    type: 'security_hardening',
    priority: 'high',
    description: 'Review and harden security aspects',
    prompt_context: `Address security considerations: ${validation.action_items.filter(a => a.category === 'security').map(a => a.task).join('; ')}`
  },
  {
    type: 'performance_optimization',
    priority: 'low',
    description: 'Profile and optimize critical paths',
    prompt_context: 'Add benchmarks and optimize bottlenecks'
  }
];

return improvementTasks.map(task => ({
  json: {
    parent_packet_id: item.packet_id,
    issue_id: item.issue_id,
    phase: 'improvement',
    task: task,
    original_implementation: item.implementation.filename,
    validation_results: validation
  }
}));

// ITERATE PATH: Generate focused improvement tasks
const item = $input.first().json;
const validation = item.validation;

const iterationTasks = validation.action_items
  .filter(action => action.priority === 'high' || action.priority === 'medium')
  .map(action => ({
    json: {
      parent_packet_id: item.packet_id,
      issue_id: item.issue_id,
      phase: 'iteration',
      task: {
        type: 'focused_improvement',
        priority: action.priority,
        description: action.task,
        rationale: action.rationale,
        category: action.category || 'general'
      },
      original_implementation: item.implementation.filename,
      validation_results: validation,
      critical_issues: validation.critical_issues
    }
  }));

return iterationTasks;

// REJECT PATH: Analyze failure and create corrective action
const item = $input.first().json;
const validation = item.validation;

// Build detailed failure analysis
const failureAnalysis = {
  original_prompt: item.prompts.user,
  critical_issues: validation.critical_issues,
  quality_score: validation.overall_quality_score,
  direction_issues: validation.direction_assessment,
  retry_count: item.retry_count + 1
};

// Create improved prompt with lessons learned
const improvedSystemPrompt = `You are an expert software engineer. A previous implementation attempt FAILED validation.

## Why the Previous Attempt Failed
${validation.critical_issues.map(issue => `- [${issue.severity}] ${issue.category}: ${issue.description}`).join('\n')}

## Direction Concerns
${validation.direction_assessment}

## What You Must Do Differently
1. ${validation.action_items.slice(0, 5).map(a => a.task).join('\n2. ')}

## Critical Requirements
- Address EVERY critical issue listed above
- Test your logic before claiming it works
- Use concrete, tested implementations
- No placeholders or TODOs
- Verify all imports and dependencies
- Check for deprecated APIs

This is attempt #${failureAnalysis.retry_count}. Make it count.`;

return {
  json: {
    parent_packet_id: item.packet_id,
    issue_id: item.issue_id,
    phase: 'correction',
    retry_count: failureAnalysis.retry_count,
    failure_analysis: failureAnalysis,
    improved_prompts: {
      system: improvedSystemPrompt,
      user: item.prompts.user // Keep original user prompt
    },
    previous_attempts: [...(item.previous_attempts || []), {
      attempt: item.retry_count,
      timestamp: item.implementation.timestamp,
      quality_score: validation.overall_quality_score,
      issues: validation.critical_issues
    }]
  }
};

// Execute correction - similar to initial implementation but with improved prompts
const item = $input.first().json;

return {
  json: {
    ...item,
    model_config: {
      model: 'gpt-oss-20b',
      temperature: 0.2, // Lower temperature for more focused correction
      max_tokens: 8000
    },
    prompts: item.improved_prompts
  }
};

// Format correction output and loop back to validation
const item = $input.first();
const response = item.json.choices[0].message.content;
const metadata = $node["Prepare Correction"].json;

const timestamp = new Date().toISOString();
const outputFilename = `${metadata.parent_packet_id}-correction-attempt-${metadata.retry_count}.md`;

return {
  json: {
    ...metadata,
    implementation: {
      raw_output: response,
      timestamp: timestamp,
      filename: outputFilename,
      model: metadata.model_config.model,
      phase: 'validation',
      is_correction: true
    }
  }
};

// Generate summary report
const allItems = $input.all();

const summary = {
  total_processed: allItems.length,
  accepted: allItems.filter(i => i.json.validation?.recommendation === 'ACCEPT').length,
  needs_iteration: allItems.filter(i => i.json.validation?.recommendation === 'ITERATE').length,
  rejected: allItems.filter(i => i.json.validation?.recommendation === 'REJECT').length,
  avg_quality_score: allItems.reduce((sum, i) => sum + (i.json.validation?.overall_quality_score || 0), 0) / allItems.length,
  critical_issues_total: allItems.reduce((sum, i) => sum + (i.json.validation?.critical_issues?.length || 0), 0),
  outputs: allItems.map(i => ({
    issue_id: i.json.issue_id,
    title: i.json.title,
    recommendation: i.json.validation?.recommendation,
    quality_score: i.json.validation?.overall_quality_score,
    output_file: i.json.implementation?.filename,
    critical_issues: i.json.validation?.critical_issues?.length || 0
  })),
  timestamp: new Date().toISOString()
};

return { json: summary };
