// Check if we have a task to process
const items = $input.all();

if (!items || items.length === 0 || !items[0].json.id) {
  return [{
    json: {
      hasTask: false,
      message: 'No pending tasks in queue'
    }
  }];
}

const task = items[0].json;

return [{
  json: {
    hasTask: true,
    taskId: task.id,
    projectName: task.project_name || 'NewProject',
    description: task.description || '',
    requirements: task.requirements ? JSON.parse(task.requirements) : [],
    acceptanceCriteria: task.acceptance_criteria ? JSON.parse(task.acceptance_criteria) : [],
    linearIssueId: task.linear_issue_id,
    priority: task.priority || 'medium',
    iteration: 0,
    maxIterations: 5,
    qualityThreshold: 85
  }
}];

// Detect libraries for Context7 query
const task = $json;
const text = (task.description + ' ' + JSON.stringify(task.requirements)).toLowerCase();

const libraryPatterns = [
  { name: 'react', pattern: /\breact\b/i, context7Id: '/facebook/react' },
  { name: 'fastapi', pattern: /\bfastapi\b/i, context7Id: '/tiangolo/fastapi' },
  { name: 'vite', pattern: /\bvite\b/i, context7Id: '/vitejs/vite' },
  { name: 'material-ui', pattern: /\bmaterial[-\s]?ui|\bmui\b/i, context7Id: '/mui/material-ui' },
  { name: 'three.js', pattern: /\bthree\.?js|3d|stl\b/i, context7Id: '/mrdoob/three.js' },
  { name: 'typescript', pattern: /\btypescript\b/i, context7Id: '/microsoft/TypeScript' },
  { name: 'tailwind', pattern: /\btailwind/i, context7Id: '/tailwindlabs/tailwindcss' },
  { name: 'axios', pattern: /\baxios\b/i, context7Id: '/axios/axios' },
  { name: 'express', pattern: /\bexpress\b/i, context7Id: '/expressjs/express' },
  { name: 'next.js', pattern: /\bnext\.?js\b/i, context7Id: '/vercel/next.js' },
  { name: 'prisma', pattern: /\bprisma\b/i, context7Id: '/prisma/prisma' },
  { name: 'stripe', pattern: /\bstripe\b/i, context7Id: '/stripe/stripe-node' }
];

const detectedLibraries = libraryPatterns.filter(lib => lib.pattern.test(text));

return [{
  json: {
    ...task,
    detectedLibraries: detectedLibraries.map(l => l.name),
    libraryIds: detectedLibraries.map(l => l.context7Id)
  }
}];

// Merge Context7 docs with task data
const task = $('Detect Libraries for Context7').first().json;
let docs = '';

try {
  const context7Response = $('Query Context7 (HTTP)').first().json;
  if (context7Response && context7Response.content) {
    docs = context7Response.content;
  }
} catch (e) {
  // Context7 not available, continue without docs
  docs = '';
}

return [{
  json: {
    ...task,
    context7Docs: docs,
    hasDocumentation: docs.length > 0
  }
}];

// Prepare comprehensive build prompt
const task = $json;

const buildPrompt = `
=== PROJECT BUILD REQUEST ===

PROJECT: ${task.projectName}
ITERATION: ${task.iteration + 1} of ${task.maxIterations}

=== DESCRIPTION ===
${task.description}

=== REQUIREMENTS ===
${task.requirements.map((r, i) => `${i+1}. ${r}`).join('\n')}

=== ACCEPTANCE CRITERIA ===
${task.acceptanceCriteria.map((c, i) => `${i+1}. ${c}`).join('\n')}

${task.previousFeedback ? `
=== FEEDBACK FROM PREVIOUS ITERATION ===
${task.previousFeedback}
` : ''}

${task.context7Docs ? `
=== OFFICIAL DOCUMENTATION (Context7) ===
${task.context7Docs.substring(0, 4000)}
` : ''}

=== OUTPUT FORMAT ===

Return ONLY valid JSON with this structure:
{
  "status": "completed",
  "filesCreated": [
    {
      "path": "src/components/Example.tsx",
      "content": "// COMPLETE file content here",
      "purpose": "Main component"
    }
  ],
  "setupCommands": ["npm install", "npm run dev"],
  "qualityChecklist": {
    "responsive": true,
    "errorHandling": true,
    "loadingStates": true,
    "formValidation": true,
    "accessibleUI": true
  },
  "testInstructions": "Steps to verify the implementation"
}

DELIVER PRODUCTION-QUALITY CODE. ALL features must be FULLY IMPLEMENTED.
`;

return [{
  json: {
    ...task,
    buildPrompt
  }
}];

// Parse and validate builder output
const task = $('Prepare Build Prompt').first().json;
const rawOutput = $json.output || $json.text || '';

let parsed;
try {
  // Extract JSON from response
  const jsonMatch = rawOutput.match(/\{[\s\S]*\}/m);
  if (!jsonMatch) {
    throw new Error('No JSON found in response');
  }

  parsed = JSON.parse(jsonMatch[0]);

  // Validate structure
  if (!parsed.filesCreated || !Array.isArray(parsed.filesCreated)) {
    throw new Error('Missing filesCreated array');
  }

  if (parsed.filesCreated.length === 0) {
    throw new Error('No files generated');
  }

  // Validate each file has content
  for (const file of parsed.filesCreated) {
    if (!file.path || !file.content) {
      throw new Error(`Invalid file entry: ${JSON.stringify(file).substring(0, 100)}`);
    }
    if (file.content.includes('TODO') || file.content.includes('// implement')) {
      parsed._hasStubs = true;
    }
  }

} catch (e) {
  parsed = {
    status: 'error',
    error: e.message,
    rawOutput: rawOutput.substring(0, 1000),
    filesCreated: []
  };
}

return [{
  json: {
    ...task,
    builderOutput: parsed,
    filesToWrite: parsed.filesCreated || [],
    hasError: parsed.status === 'error',
    iteration: task.iteration + 1
  }
}];

// Write files to project directory
const fs = require('fs');
const path = require('path');

const task = $json;
const projectPath = `/mnt/n8n-nas/generated_apps/${task.projectName}`;
const files = task.filesToWrite || [];

// Clean and create project directory
fs.rmSync(projectPath, { recursive: true, force: true });
fs.mkdirSync(projectPath, { recursive: true });

const results = { written: [], errors: [] };

for (const file of files) {
  try {
    const fullPath = path.join(projectPath, file.path);
    fs.mkdirSync(path.dirname(fullPath), { recursive: true });
    fs.writeFileSync(fullPath, file.content, 'utf8');
    results.written.push(file.path);
  } catch (e) {
    results.errors.push({ path: file.path, error: e.message });
  }
}

// Create package.json if not exists
const pkgPath = path.join(projectPath, 'package.json');
if (!fs.existsSync(pkgPath)) {
  const pkg = {
    name: task.projectName.toLowerCase().replace(/[^a-z0-9-]/g, '-'),
    version: '1.0.0',
    type: 'module',
    scripts: {
      dev: 'vite',
      build: 'vite build',
      preview: 'vite preview'
    },
    dependencies: {
      'react': '^18.2.0',
      'react-dom': '^18.2.0'
    },
    devDependencies: {
      '@vitejs/plugin-react': '^4.0.0',
      'vite': '^5.0.0',
      'typescript': '^5.0.0'
    }
  };
  fs.writeFileSync(pkgPath, JSON.stringify(pkg, null, 2));
  results.written.push('package.json');
}

return [{
  json: {
    ...task,
    projectPath,
    filesWritten: results.written,
    writeErrors: results.errors,
    readyForBuild: results.errors.length === 0
  }
}];

// Evaluate build result and run Playwright tests
const task = $('Write Project Files').first().json;
const buildOutput = $json.stdout || '';

const buildFailed = buildOutput.includes('BUILD_ERROR') || 
                    buildOutput.includes('error TS') ||
                    buildOutput.includes('SyntaxError');

let qualityScore = 0;
const checks = [];

// Check 1: Build succeeds
if (!buildFailed) {
  qualityScore += 30;
  checks.push('Build succeeds');
} else {
  checks.push('BUILD FAILED: ' + buildOutput.substring(0, 200));
}

// Check 2: Files were written
if (task.filesWritten.length >= 3) {
  qualityScore += 20;
  checks.push(`${task.filesWritten.length} files written`);
}

// Check 3: Has main entry point
const hasEntry = task.filesWritten.some(f => 
  f.includes('main.') || f.includes('index.') || f.includes('App.')
);
if (hasEntry) {
  qualityScore += 20;
  checks.push('Has entry point');
}

// Check 4: Has components
const hasComponents = task.filesWritten.some(f => f.includes('/components/'));
if (hasComponents) {
  qualityScore += 15;
  checks.push('Has component structure');
}

// Check 5: Has styling
const hasStyling = task.filesWritten.some(f => 
  f.includes('.css') || f.includes('styled') || f.includes('theme')
);
if (hasStyling) {
  qualityScore += 15;
  checks.push('Has styling');
}

const passesThreshold = qualityScore >= task.qualityThreshold;
const canIterate = task.iteration < task.maxIterations;

return [{
  json: {
    ...task,
    buildOutput: buildOutput.substring(0, 2000),
    buildFailed,
    qualityScore,
    qualityChecks: checks,
    passesThreshold,
    canIterate,
    needsIteration: !passesThreshold && canIterate
  }
}];

// Prepare feedback for next iteration
const task = $json;

return [{
  json: {
    taskId: task.taskId,
    projectName: task.projectName,
    description: task.description,
    requirements: task.requirements,
    acceptanceCriteria: task.acceptanceCriteria,
    context7Docs: task.context7Docs,
    detectedLibraries: task.detectedLibraries,
    iteration: task.iteration,
    maxIterations: task.maxIterations,
    qualityThreshold: task.qualityThreshold,
    previousFeedback: `
=== ITERATION ${task.iteration} FEEDBACK ===

Quality Score: ${task.qualityScore}% (need ${task.qualityThreshold}%)

CHECKS:
${task.qualityChecks.map(c => '- ' + c).join('\n')}

${task.buildFailed ? `
BUILD ERRORS:
${task.buildOutput}

FIX THESE ERRORS AND RETRY.
` : 'Build succeeded but quality score too low.'}

IMPROVEMENTS NEEDED:
- Ensure all imports are correct
- Add proper TypeScript types
- Include all required dependencies
- Make sure the app actually runs
`
  }
}];

// Success! Update task status and prepare for commit
const task = $json;

return [{
  json: {
    status: 'completed',
    taskId: task.taskId,
    projectName: task.projectName,
    projectPath: task.projectPath,
    iterations: task.iteration,
    qualityScore: task.qualityScore,
    qualityChecks: task.qualityChecks,
    filesWritten: task.filesWritten,
    commitMessage: `feat(${task.projectName}): Initial build - Quality ${task.qualityScore}%`
  }
}];

// Log completion to work log
const fs = require('fs');
const task = $json;

const logEntry = {
  timestamp: new Date().toISOString(),
  taskId: task.taskId,
  projectName: task.projectName,
  projectPath: task.projectPath,
  status: 'completed',
  qualityScore: task.qualityScore,
  iterations: task.iterations,
  filesWritten: task.filesWritten.length,
  qualityChecks: task.qualityChecks
};

// Append to log file
const logPath = '/mnt/n8n-nas/app_builder_log.json';
let log = [];
try {
  if (fs.existsSync(logPath)) {
    log = JSON.parse(fs.readFileSync(logPath, 'utf8'));
  }
} catch (e) { log = []; }

log.push(logEntry);
if (log.length > 500) log = log.slice(-500);

fs.writeFileSync(logPath, JSON.stringify(log, null, 2));

return [{ json: { ...task, logged: true } }];

